import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { HttpClientModule, HttpClient, HttpHeaders } from '@angular/common/http'
import { coffeeBuyResponse } from '../models/coffeeBuyResponse.model';
import { environment } from '../environment/environment';

@Injectable({
  providedIn: 'root'
})
export class CoffeerequestService {

  httpOptions = { Headers: new HttpHeaders}

  constructor(private http: HttpClient) { 
    http.head.arguments("Allow-Access-Control", "http://localhost");
  }

  postBuyCoffeeButton() : Observable<coffeeBuyResponse> {
    return this.http.post<coffeeBuyResponse>(`${environment.baseURL}/CoffeeBuy/BuyCoffee`, "");
  }
}
