import { Component } from '@angular/core';
import { CoffeerequestService } from './coffee/coffeerequest.service';
import { coffeeBuyResponse } from './models/coffeeBuyResponse.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  providers: [CoffeerequestService]
})
export class AppComponent {
  title = 'todo';

  hasProcessed: boolean;
  responseMessage: string;


  constructor(private coffeeBuyService: CoffeerequestService) {
    this.hasProcessed = false;
    this.responseMessage = '';
  }

  onUpdate() {
    this.coffeeBuyService.postBuyCoffeeButton().subscribe( res => {
      if (res.result) {
        this.hasProcessed = true;
        this.responseMessage = res.message + ' ID:' + res.uuid;
      }
      console.log(res);
      console.log(this.hasProcessed);
    }, err => {
      console.log(err);
    });
  }
}
<div class="flex items-center justify-center h-screen text-4xl">
  <div class="bg-orange-200 shadow-md rounded-lg p-6">
    <div class="text-orange-700 ">Coffee for every one</div>
    <img src="../assets/l-intro-1645231221.jpg" alt="coffee" class="h-auto mx-auto max-w-md rounded-lg">
    <p>Hello and welcome with angular, please go ahead and buy your coffee</p>
    <div *ngIf="!hasProcessed">
      <button class="w-16 mt-4 mx-auto block bg-orange-950 text-white rounded-lg" (click)="onUpdate()">Buy</button>
    </div>
    <div *ngIf="hasProcessed">
      <div class="justify-items-center text-green-900 bg-green-200 rounded-lg mt-2 p-2">
        <p class="font-thin">{{responseMessage}}</p>
     </div>
    </div>
  </div>
</div>
<router-outlet></router-outlet>
